!function($) {
    "use strict";

    // Toggle sidebar when clicking on #sidebarToggleTop
    $("#sidebarToggleTop").on("click", function(e) {
        $("body").toggleClass("sidebar-toggled");
        $(".sidebar").toggleClass("toggled");

        // Adjust content width when sidebar is toggled
        if ($(".sidebar").hasClass("toggled")) {
            $(".content").css("margin-left", "56px"); // Adjust this value as needed
        } else {
            $(".content").css("margin-left", "220px"); // Adjust this value as needed
        }

        // Hide collapsed sidebar items
        if ($(".sidebar").hasClass("toggled")) {
            $(".sidebar .collapse").collapse("hide");
        }
    });

    // Resize event listener
    $(window).resize(function() {
        if ($(window).width() < 480) {
            // Very small screens: hide sidebar
            $("body").addClass("sidebar-toggled");
            $(".sidebar").addClass("toggled");
            $(".sidebar .collapse").collapse("hide");
            $(".content").css("margin-left", "56px"); // Adjust this value as needed
        } else {
            // Medium and larger screens: show sidebar
            $("body").removeClass("sidebar-toggled");
            $(".sidebar").removeClass("toggled");
            $(".sidebar .collapse").collapse("show");
            $(".content").css("margin-left", "220px"); // Adjust this value as needed
        }
    }).resize(); // Trigger the resize handler on page load

    // Smooth scroll to top
    $(document).on("scroll", function() {
        if ($(this).scrollTop() > 100) {
            $(".scroll-to-top").fadeIn();
        } else {
            $(".scroll-to-top").fadeOut();
        }
    });

    $(document).on("click", "a.scroll-to-top", function(e) {
        var target = $(this).attr("href");
        $("html, body").stop().animate({
            scrollTop: $(target).offset().top
        }, 1000, "easeInOutExpo");
        e.preventDefault();
    });
}(jQuery);
